import { atom, selector } from "recoil";

interface IToDoState {
    [key: string]: ITodo[];
}

interface ITodo {
    id: number;
    text: string;
}

export const toDoState = atom<IToDoState>({
    key: "toDo",
    default: {
        "To Do": [],
        Doing: [],
        Done: []
    }
})
